import React, { Component } from 'react';
class ExperienceCard extends Component {
  render() {
    const {
      city,
      about,
      name
    } = this.props; 

    return (
      <div style={{textAlign:"right",height:"100px",overflow:"auto",padding:"1rem",margin:"0 .3rem",borderRadius: "10px",boxShadow: "-2px 4px 15px -3px rgba(0, 0, 0, 0.47)"}}>
          <React.Fragment>
                {city ? (
                  <React.Fragment>
                    <p style={{fontWeight:"bolder",color:"blue"}}>:{name}</p>
                    <p style={{color:"gray",fontWeight:"bold"}}>{city}</p>
                    <p style={{fontSize:"13px"}}>{this.props.description}</p>
                  </React.Fragment>
                ) : (
                  <React.Fragment>
                    <p >{about}</p>
                    <p >{this.props.description}</p>
                  </React.Fragment>
                )}
          </React.Fragment>
      </div>
    );
  }
}

export default ExperienceCard;
