import React, { Component } from 'react';
import Slider from 'react-slick';
import ExperienceCard from './ExperienceCard';
import { Left } from './Arrows.js';
import { Right } from './Arrows';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Styles/headout-picks.css';
import './Styles/topic.css';

class Categories extends Component {
    render() {
        const { Category } = this.props;
        return (
            <div style={{ width: "95%", margin: "0 2.5%", }}>
                <p className='topic'>{this.props.topic}</p>
                <PickedSlider Category={Category} />
            </div>
        );
    }
}

class PickedSlider extends React.Component {
    render() {
        var settings = {
            infinite: true,
            speed: 500,
            slidesToShow: 4,
            slidesToScroll: 4,
            nextArrow: <Right />,
            prevArrow: <Left />
        };
        const { Category } = this.props;
        return (
            <Slider {...settings} >
                {
                    Category.map(({ name }) => (
                        <span
                            style={{
                                textAlign: "right",
                                height: "fit-content",
                                overflow: "auto",
                                padding: "1rem",
                                margin: ".3rem",
                                borderRadius: "10px",
                                boxShadow: "-2px 4px 15px -3px rgba(0, 0, 0, 0.47)"
                            }}>
                            {name}
                        </span>
                    )
                    )}
            </Slider>
        );
    }
}

export default Categories;
