import React from "react";
import "../../assets/button.scss"



export default class ButtonContainer extends React.Component {
    render() {
        const { btnBoxClass, direction } = this.props
        return (
            <div className={btnBoxClass} style={{ flexDirection: direction }}>
                {this.props.children}
            </div>
        )
    }
}
