import React, { Component } from 'react';
import Header from './Header/index';
import Collections from './Collections';
import Footer from './footer';
import './Styles/shiraz.css';
import TextExpand from './textContainder';

class Shiraz extends Component {
  componentDidMount() {
    window.scrollTo(0, 0);
  }
  render() {
    return (

      <>
        <Header
          backgroundImagesData={backgroundImagesData}
          history={this.props.history}
          selectedCity={'shiraz'}
          navigationData={ShirazNavigationData}
          show={false}
        />
        <div className="new-york-wrapper">
          <Collections collectionsData={collectionsData} show={false} />
          <img src={require("../Components/Images/hotel.png")} width="95%" />
          <TextExpand text={Text} />
        </div>
        <Footer />
      </>
    );
  }
}

