import React, { useState } from 'react';

export function Text() {
    const [showAll, setShowAll] = useState(false);

    const handleClick = () => {
        setShowAll(!showAll);
    };

    return (
        <div>
            <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed vel
                mauris lectus. Integer nec erat non magna sodales bibendum. Nam
                consectetur tellus sit amet nisi bibendum, vel tincidunt libero
                tempus. Donec non ante vel velit varius aliquet. Curabitur
                ullamcorper ornare lectus eu commodo. Quisque vel felis vitae
                tortor euismod commodo. Duis nec nulla eget turpis semper
                facilisis.
            </p>
            {!showAll && (
                <p onClick={handleClick} style={{ cursor: 'pointer' }}>
                    Show more
                </p>
            )}
            {showAll && (
                <p onClick={handleClick} style={{ cursor: 'pointer' }}>
                    Show less
                </p>
            )}
            {showAll && (
                <p>
                    Fusce id bibendum velit. Donec et egestas quam. Fusce nec nulla
                    sed odio lacinia gravida. Sed eget purus quis quam tincidunt
                    vehicula. Aliquam erat volutpat. Sed dignissim pharetra dolor,
                    ut mattis nunc fringilla at. Nulla facilisi.
                </p>
            )}
        </div>
    );
};
