import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Select from 'react-select';
import Background from './Background';
import Logo from '../Images/logo.png';
import MobileApp from '../Images/mobile-app.gif';
import '../Styles/Header.css';

class Header extends Component {
  state = {
    experience: ''
  };

  componentDidMount() {
    window.scrollTo(0, 0);
  }

  changeExperience = event => this.setState({ experience: event.target.value });

  render() {
    const {
      backgroundImagesData,
      navigationData,
      selectedCity,
      history
    } = this.props;
    return (
      <React.Fragment>
        <HeaderNav
          changeExperience={this.changeExperience}
          experience={this.state.experience}
          history={history}
          selectedCity={selectedCity}
          navigationData={navigationData}
        />
        <Background backgroundImagesData={backgroundImagesData} />
        <div className="search-bar-div">
          <div className="select-city-large">
            <i className="fas fa-map-marker" />
            <Searchbar
              style={customStyles}
              history={this.props.history}
              selectedCity={this.props.selectedCity}
            />
          </div>
          <div className="select-experience-large">
            <input
              type="text"
              placeholder="جستجو کنید..."
              onChange={this.changeExperience}
              value={this.state.experience}
            />
            <i className="fas fa-search" />
          </div>
          <button id="go">Let's Go</button>
        </div>
      </React.Fragment>
    );
  }
}

export class HeaderNav extends Component {
  render() {
    return (
      <div className="top-navbar">
        <div className="top-navbar--logo">
          <img src={require("../../assets/images/png/logo.png")} />
        </div>
        <div className="top-navbar--options">
          <div className="top-navbar--option">
            <img src={require("../../assets/images/png/message.png")} width="20px" />
            <span>پشتیبانی 021-23243456</span>
          </div>
          <div className="top-navbar--option">
            <img src={require("../../assets/images/png/heart.png")} width="20px" />
            <span>مورد علاقه ها</span>
          </div>
          <div className="top-navbar--option">
            <img src={require("../../assets/images/png/phone.png")} width="22px" />
            <span>دانلود اپلیکیشن</span>
          </div>
          <div className="top-navbar--option" >
            <img src={require("../../assets/images/png/person.png")} width="20px" />
            <span>ورود/ثبت نام</span>
          </div>
        </div>
      </div>

    );
  }
}

const options = [
  { value: 'new-york', label: 'New York' },
  { value: 'بوشهر', label: 'بوشهر' },
  { value: 'مشهد', label: 'مشهد' },
  { value: 'رشت', label: 'رشت' },
  { value: 'تهران', label: 'تهران' },
  { value: 'ساری', label: 'ساری' },
  { value: 'تبریز', label: 'تبریز' },
  { value: 'شیراز', label: 'شیراز' },
  { value: 'یزد', label: 'یزد' },
  { value: 'سیستان و بلوچستان', label: 'سیستان و بلوچستان' },
  { value: 'ارومیه', label: 'ارومیه' },
  { value: 'بندرعباس', label: 'بندرعباس' },
  { value: 'لرستان', label: 'لرستان' },
  { value: 'کردستان', label: 'کردستان' }
];

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    borderBottom: 'none',
    color: state.isSelected ? 'red' : '#727272',
    padding: 10,
    cursor: 'pointer',
    background: state.isSelected ? 'white' : 'white',
    fontSize: '13px',
    textAlign: 'left',
    width: 120
  }),
  control: () => ({
    width: 150,
    display: 'flex',
    fontSize: '14px',
    marginTop: '10px',
    paddingLeft: '5px'
  }),
  singleValue: (provided, state) => {
    const opacity = state.isDisabled ? 0.5 : 1;
    const transition = 'opacity 300ms';

    return { ...provided, opacity, transition };
  }
};

const smallSearchbar = {
  option: (provided, state) => ({
    ...provided,
    borderBottom: 'none',
    color: state.isSelected ? 'red' : '#727272',
    padding: 10,
    cursor: 'pointer',
    background: state.isSelected ? 'white' : 'white',
    fontSize: '13px',
    textAlign: 'left',
    paddingLeft: '20px',
    width: 150
  }),
  control: () => ({
    width: 150,
    display: 'flex',
    fontSize: '11px',
    marginLeft: '15px'
  }),
  singleValue: (provided, state) => {
    const opacity = state.isDisabled ? 0.5 : 1;
    const transition = 'opacity 300ms';

    return { ...provided, opacity, transition };
  }
};

class Searchbar extends Component {
  state = {
    selectedOption: null
  };

  handleChange = selectedOption => {
    this.setState({ selectedOption: selectedOption });
    this.changeUrl(selectedOption.value);
  };

  changeUrl = url => {
    this.props.history.push(`/cities/${url}`);
  };

  render() {
    const { selectedOption } = this.state;
    const { selectedCity } = this.props;
    if (selectedCity) {
      return (
        <Select
          styles={this.props.style}
          placeholder={selectedCity}
          value={selectedOption}
          onChange={this.handleChange}
          options={options}
          className="city-select-dropdown"
        />
      );
    } else {
      return (
        <Select
          styles={this.props.style}
          placeholder="شهر موردنطرتان را انتخاب کنید."
          value={selectedOption}
          onChange={this.handleChange}
          options={options}
          className="city-select-dropdown"
        />
      );
    }
  }
}

export default Header;
