import React, { Component } from 'react';
import Routes from './CityRoutes';

class App extends Component {
  state = { width: 0, height: 0 };

  componentDidMount() {
    this.updateWindowDimensions();
    window.addEventListener('resize', this.updateWindowDimensions);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.updateWindowDimensions);
  }
  updateWindowDimensions = () =>
    this.setState({ width: window.innerWidth, height: window.innerHeight });

  render() {
    const { width } = this.state;
    const mobileTablet = width <= 1100;

    if (mobileTablet) {
      return (
        <div style={{display:"flex",flexDirection:"row",justifyContent:"center",margin:"auto"}}>
          <img src={require('./Components/Images/logo.png')} />
          <p>
            لطفااندازه صفحه نمایش را بزرگتر کنید.
          </p>
        </div>
      );
    } else {
      return <Routes />;
    }
  }
}

export default App;
