import React, { Component } from 'react';

class ExperienceCard extends Component {
  state = {
    listed: false
  };

  addtoWishlist = () =>
    this.setState(prevState => ({ listed: !prevState.listed }));

  render() {
    const url = `url(${this.props.url})`;
    const {
      discount,
      cashback,
      currency,
      lastPrice,
      ratings,
      stars,
      city,
      about,
      showMore,
      highlight
    } = this.props;

    let style;

    if (this.state.listed) {
      style = {
        color: '#f43361',
        fontSize: '20px',
        position: 'absolute',
        right: '10px',
        top: '15px',
        fontWeight: '700',
        transition: 'transform 0.3s ease-in-out'
      };
    } else {
      style = {
        color: 'white',
        fontSize: '20px',
        position: 'absolute',
        right: '10px',
        top: '15px',
        fontWeight: '400',
        transition: 'transform 0.3s ease-in-out'
      };
    }

    return (
      <div className="exp-card">
          <React.Fragment>

            <div className="exp-content-wrap">
              <div className="exp-info-wrap">
                {city ? (
                  <React.Fragment>
                    <p id="exp-city">{city}</p>
                    <p id="exp-description">{this.props.description}</p>
                  </React.Fragment>
                ) : (
                  <React.Fragment>
                    <p id="exp-about">{about}</p>
                    <p id="exp-description">{this.props.description}</p>
                  </React.Fragment>
                )}
              </div>
            </div>
          </React.Fragment>
      </div>
    );
  }
}

export default ExperienceCard;
