import React from 'react';

class TextExpand extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expanded: false
    };
    this.handleExpandClick = this.handleExpandClick.bind(this);
  }

  handleExpandClick() {
    this.setState({ expanded: true });
  }

  render() {
    const { text } = this.props;
    const { expanded } = this.state;
    const shortText = text.substring(0, 300); // Change 100 to however many characters you want to display initially
    return (
      <div>
        <p>{expanded ? text : shortText}</p>
        {!expanded && (
          <button onClick={this.handleExpandClick}>.</button>
        )}
      </div>
    );
  }
}

export default TextExpand;
