import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import { Left, Right } from './Arrows';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Styles/top-cities.css';

class TopCities extends Component {
  render() {
    return (
      <div>
        <div className="top-cities-wrapper">
          <h1>شهرهای پرطرفدار:</h1>
          <div className="top-cities-carousel-wrap">
            <CitySlider />
          </div>
          <hr
            style={{
              height: '1px',
              color: '#e7e7e7',
              borderTop: 'none',
              borderLeft: 'none'
            }}
          />
        </div>
      </div>
    );
  }
}

class CitySlider extends React.Component {
  render() {
    var settings = {
      infinite: true,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5,
      nextArrow: <Right />,
      prevArrow: <Left />
    };
    return (
      <Slider {...settings}>
        {topCitiesData &&
          topCitiesData.map(({ id, city, url, description, route }) => (
            <Link
              to={{ pathname: `/cities/${route}` }}
              key={id}
              className="link"
            >
              <CityCard city={city} url={url} description={description} />
            </Link>
          ))}
      </Slider>
    );
  }
}

class CityCard extends Component {
  render() {
    const url = `url(${this.props.url})`;
    return (
      <div className="city-card-wrapper">
        <div className="city-card">
          <div
            className="city-card-img"
            style={{
              backgroundImage: url
            }}
          ></div>
          <div className="city-details">
            <p style={{color:"black",fontWeight:"bold"}}>{this.props.city}</p>
            <div>
              <p>{this.props.description}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const topCitiesData = [
  {
    id: 1,
    city: 'شیراز',
    route: 'shiraz',
    description: "باغ ارم",
    url:
    'https://www.chetor.com/wp-content/uploads/2018/07/Eram-garden-Shiraz.jpg'
  },
  {
    id: 2,
    city: 'قم',
    route: 'ghom',
    description: '(حرم حضرت معصومه (س',
    url:
      'https://images.kojaro.com/2016/9/b43d58d1-4a6e-426a-8371-ea4158fc57f9.jpg'
  },
  {
    id: 3,
    city: 'شاهرود',
    route: 'shahrood',
    description: 'جنگل ابرها',
    url:
      'https://www.raheeno.com/Uploadfiles/editor/Jungle-abr-min.jpg'
  },
  {
    id: 4,
    city: 'هرمزگان',
    route: 'hormozgan',
    description: "تنگه بوچیر",
    url:
      'https://media.karnaval.ir/uploads/2020/09/0d8cc816-e479-42dd-b20d-264799f4f5a1.jpg?size=400x200'
  },
  {
    id: 5,
    city: 'جزیره هرمز',
    route: 'hormoz',
    description: 'کوه های رنگی',
    url:
      'https://newsmedia.tasnimnews.com/Tasnim/Uploaded/Image/1400/09/01/1400090114563818024109094.jpg'
  },
  {
    id: 6,
    city: 'یزد',
    route: 'yazd',
    description: 'سرو 1400 ساله ابرکوه',
    url:
      'https://images.kojaro.com/2020/10/ce22849d-59c1-4a27-98ce-2a420d412a71.jpg'
  },
  {
    id: 7,
    city: 'تهران',
    route: 'tehran',
    description: 'دریاچه لزور فیروزکوه',
    url:
      'https://villarabet.com/wp-content/uploads/2021/06/%D8%AF%D8%B1%DB%8C%D8%A7%DA%86%D9%87-%D9%84%D8%B2%D9%88%D8%B1-%DA%A9%D8%AC%D8%A7%D8%B3%D8%AA-%D8%AF%D8%B1%DB%8C%D8%A7%DA%86%D9%87-%D9%84%D8%B2%D9%88%D8%B1-%D8%AA%D9%87%D8%B1%D8%A7%D9%86-%D8%AF%D8%B1%DB%8C%D8%A7%DA%86%D9%87-%D9%84%D8%B2%D9%88%D8%B1-%D9%81%DB%8C%D8%B1%D9%88%D8%B2%DA%A9%D9%88%D9%87-%D8%AF%D8%B1%DB%8C%D8%A7%DA%86%D9%87-%D9%84%D8%B2%D9%88%D8%B1-%D8%AF%D8%B1-%D8%AA%D9%87%D8%B1%D8%A7%D9%86-%D8%AF%D8%B1%DB%8C%D8%A7%DA%86%D9%87-%D9%84%D8%B2%D9%88%D8%B11.jpg'
  },
  {
    id: 8,
    city: 'مازندران',
    route: 'mazandran',
    description: 'تله کابین رامسر',
    url:
      'https://www.jabama.com/mag/wp-content/uploads/2021/12/ramsar-telecabine-facilities.jpg'
  }
];

export default TopCities;
